# import datafrom __future__ import print_functionfrom sklearn.datasets import load_digitsfrom sklearn.model_selection import train_test_splitfrom sklearn.preprocessing import LabelBinarizerimport tensorflow as tfimport numpy as npimport matplotlib.pyplot as pltkeep_prob = tf.placeholder(tf.float32)def add_layer(inputs, in_size, out_size, n_layer, activation_function=tf.nn.relu):    layer_name = 'layer%s' % n_layer  ## define a new var    with tf.name_scope("layer"):        with tf.name_scope("weights"):            Weights = tf.Variable(tf.random_normal([in_size, out_size]), name="W")            tf.summary.histogram(layer_name + '/weights', Weights)  # tensorflow >= 0.12        with tf.name_scope("biases"):            biases = tf.Variable(tf.random_normal([1, out_size]) + 0.1, name="b")            tf.summary.histogram(layer_name + '/biases', biases)  # Tensorflow >= 0.12        with tf.name_scope("Wx_plus_b"):            Wx_plus_b = tf.matmul(inputs, Weights) + biases            #dropout            Wx_plus_b = tf.nn.dropout(Wx_plus_b, keep_prob)        if activation_function is None:            outputs = Wx_plus_b        else:            outputs = activation_function(Wx_plus_b)        tf.summary.histogram(layer_name + '/outputs', outputs)  # Tensorflow >= 0.12        return outputsdef compute_accuracy(v_xs, v_ys):    global prediction    y_pre = session.run(prediction, feed_dict={xs: v_xs})    with tf.name_scope("correct_prediction"):        correct_prediction = tf.equal(tf.argmax(y_pre, 1), tf.argmax(v_ys, 1))        tf.summary.histogram('correct', tf.cast(correct_prediction, tf.float32))  # tensorflow >= 0.12    accrucy = tf.reduce_mean(tf.cast(correct_prediction, tf.float32))    result = session.run(accrucy)    return resultdigits = load_digits()x = digits.datay = LabelBinarizer.fit_transform(digits.target)x_train, x_test, y_train, y_test = train_test_split(x, y, test_size=0.3)with tf.name_scope('inputs'):    xs = tf.placeholder(tf.float32, [None, 64], name="x_input")    ys = tf.placeholder(tf.float32, [None, 10], name="y_input")layer1 = add_layer(xs, 64, 100, "layer1", activation_function=tf.nn.tanh)prediction = add_layer(xs, 100, 10, "predition", activation_function=tf.nn.softmax)with tf.name_scope("loss"):    cross_entropy = tf.reduce_mean(-tf.reduce_sum(ys * tf.log(prediction), reduction_indices=[1]))  # loss    tf.summary.histogram('loss', cross_entropy)  # Tensorflow >= 0.12with tf.name_scope("train"):    train_step = tf.train.GradientDescentOptimizer(0.5).minimize(cross_entropy)session = tf.Session()merged = tf.summary.merge_all()  # tensorflow >= 0.12train_writer = tf.summary.FileWriter("logs/train", session.graph)test_writer = tf.summary.FileWriter("logs/test", session.graph)init = tf.global_variables_initializer()session.run(init)for i in range(1000):    session.run(train_step, feed_dict={xs: x_train, ys: y_train, keep_prob: 0.5})    if i % 50 == 0:        train_result = session.run(merged, feed_dict={xs: x_train, ys: y_train, keep_prob: 1})        test_result = session.run(merged, feed_dict={xs: x_test, ys: y_test, keep_prob: 1})        train_writer.add_summary(train_result, 1)        test_writer.add_summary(test_result, 1)session.close()